mapper.select:
SELECT IFNULL(SUM(sale_price*c.product_count), 0) money
FROM Cart_tb c, Product_tb p
WHERE c.product_num = p.product_num AND c.user_email = #{user_email}

mapper.update:
UPDATE Cart_tb
SET product_count = #{product_count}
WHERE user_name = #{user_name}
	AND product_num = #{product_num}

mapper.select:
SELECT COUNT(*)
FROM Cart_tb
WHERE user_email=#{user_email}
	AND product_num=#{product_num}

mapper.update:
UPDATE Cart_tb
SET product_count = product_count + #{product_count}
WHERE user_email=#{user_email}
	AND product_num=#{product_num}
